
public class FlowTest3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int input = 0;
		
		input = 2;
		
		System.out.println("입력하신 값은" + input);
		
		if(input % 2 != 0) {
			System.out.println("홀입니다");
		}else {
			System.out.println("짝입니다");
		}
	}

}
