
public class OperratorEx10 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
//		대입 연산자
		
		int n = 0;
		
		n += 3;
//		n = n + 3
		System.out.println(n);
		n *= 3;
//		n = n * 3
		System.out.println(n);
		n = 10;
		System.out.println(n);
		n %=3;
		n = n % 3;
		System.out.println(n);
		
		
		
	}

}
